<?php

namespace AppBundle\Repository;

use AppBundle\Entity\User;

/**
 * UserAppliedEduRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UserAppliedEduRepository extends \Doctrine\ORM\EntityRepository
{
    public function findAllForUserEducation(User $user)
    {
        $qb = $this->createQueryBuilder('favorite');

        return $qb->innerJoin('favorite.education', 'job')
            ->andWhere('favorite.user = :user')
            ->andWhere('job.isPublished = 1')
            ->orderBy('favorite.created', 'DESC')
            ->orderBy('favorite.id', 'DESC')
            ->setParameter('user', $user)
            ->getQuery()
            ->execute();
    }
}
